import React from "react";
import styled from "styled-components";
import axios from "axios";
import Pagination from "react-js-pagination";
import Select from "react-select";
import Input from "./Input";
import Button from "./Button";
import Results from "./Results";

const options = [
	{value: "movie", label: "Movie"},
	{value: "game", label: "Game"},
	{value: "series", label: "Series"}
];

const baseUrl = "https://movie-database-imdb-alternative.p.rapidapi.com";

const InputContainerStyled = styled.div`
	display: grid;
	justify-content: center;

	.select-category {
		margin-bottom: 20px;
	}
`;

class InputContainer extends React.Component {
	state = {
		searchQuery: "",
		results: [],
		totalResults: "",
		isError: false,
		errorMessage: "",
		isLoading: false,
		disabled: true,
		selectedCategory: "movie",
		currentPage: 1
	};

	// Handle onChange event on the input
	handleChange = event => {
		const {value} = event.currentTarget;
		if (!value) {
			this.setState({disabled: true, isLoading: false});
		} else {
			this.setState({disabled: false});
		}
		this.setState({searchQuery: value});
	};

	handleSelect = selectedOption => {
		this.setState({
			selectedCategory: selectedOption.value
		});
	};

	async getResults(searchQuery) {
		const {selectedCategory, currentPage} = this.state;
		if (!searchQuery) {
			this.setState({isLoading: false});
			return;
		}
		await axios({
			url: `${baseUrl}/?page=${currentPage}&r=json&type=${selectedCategory}&s=${searchQuery}`,
			method: "get",
			headers: {
				"x-rapidapi-host": "movie-database-imdb-alternative.p.rapidapi.com",
				"x-rapidapi-key": "a63832b8aamsh7a85645505609ddp1be412jsn1bc17d796591"
			}
		})
			.then(response => {
				if (response.data.Search) {
					this.setState({
						isError: false,
						results: response.data.Search,
						totalResults: response.data.totalResults,
						isLoading: false
					});
				} else {
					this.setState({
						isError: true,
						errorMessage: response.data.Error,
						isLoading: false
					});
				}
			})
			.catch(err => console.log(`this is error ${err}`));
	}

	handleClick = () => {
		const {searchQuery} = this.state;
		if (!searchQuery) {
			this.setState({isLoading: false});
			return;
		}
		this.getResults(searchQuery);
		this.setState({isLoading: true});
	};

	handlePageChange = pageNumber => {
		const {searchQuery} = this.state;
		this.getResults(searchQuery);
		this.setState({currentPage: pageNumber});
	};

	render() {
		const {
			results,
			totalResults,
			isError,
			errorMessage,
			isLoading,
			disabled,
			currentPage
		} = this.state;
		return (
			<>
				<InputContainerStyled>
					<h2>Select category</h2>
					<Select
						className="select-category"
						defaultValue={{value: "movie", label: "Movie"}}
						options={options}
						onChange={this.handleSelect}
					/>
					<Input
						handleChange={this.handleChange}
						handleClick={this.handleClick}
					/>
					<Button
						handleClick={this.handleClick}
						isLoading={isLoading}
						disabled={disabled}
					/>
					{totalResults ? <h3>Total results is {totalResults}</h3> : ""}
				</InputContainerStyled>
				<Pagination
					activePage={currentPage}
					itemsCountPerPage={10}
					totalItemsCount={totalResults}
					pageRangeDisplayed={5}
					onChange={this.handlePageChange}
				></Pagination>
				{isError ? (
					<p>{errorMessage}</p>
				) : (
					<Results results={results}></Results>
				)}
			</>
		);
	}
}
export default InputContainer;
